'use strict';

var TuneWikiApiClient = require('./client');
var TuneWikiSearchApi = require('./search');
var TuneWikiLyricsApi = require('./lyrics');

var TuneWikiApi = function (options) {
    this.client = new TuneWikiApiClient(options);
    this.search = new TuneWikiSearchApi(this.client);
    this.lyrics = new TuneWikiLyricsApi(this.client);
};

module.exports = TuneWikiApi;
