'use strict';

var TuneWikiSearchApi = function (client) {
    this.client = client;
};

TuneWikiSearchApi.prototype.search = function (type, query, cb) {
    return this.client.get('/search?type=' + type + '&q=' + query, cb);
};
TuneWikiSearchApi.prototype.lyrics = function (query, cb) {
    return this.search('lyrics', query, cb);
};
TuneWikiSearchApi.prototype.songs = function (query, cb) {
    return this.search('songs', query, cb);
};
TuneWikiSearchApi.prototype.artistsongs = function (query, cb) {
    return this.search('artistsongs', query, cb);
};
TuneWikiSearchApi.prototype.artists = function (query, cb) {
    return this.search('artists', query, cb);
};
TuneWikiSearchApi.prototype.hashtags = function (query, cb) {
    return this.search('hashtags', query, cb);
};
TuneWikiSearchApi.prototype.shares = function (query, cb) {
    return this.search('shares', query, cb);
};

module.exports = TuneWikiSearchApi;